import React, { useState } from 'react'
import {BrowserRouter as Router , Routes ,Route,Link} from 'react-router-dom'
import '../../index.css'
import './login.css'
import { useContext } from 'react'
import { UserContext } from '../Home/UserContext'
import { Button } from 'react-bootstrap';
import swal from 'sweetalert';
import { useNavigate } from 'react-router-dom';

export default function Login() {

    const {authenUser ,setAuthenUser} = useContext(UserContext)

    let navigate=useNavigate();

 const [name ,setName] =useState('');
 const [pass ,setPass] =useState(''); 

//  Check if input in name and password is null
const onChangeHandler =(fieldName ,value)=>{
    if(fieldName==='name'){
      setName(value)
    }
  
    else if(fieldName ==='pass'){
      setPass(value)
    }
  }

  const onSubmitHandler = (e)=>{
    e.preventDefault();
  
  
    if(name.trim() === '' || pass.trim() ===''){
  
      alert("required both field");
    }
    else
    {
      swal({
        title:'Welcome '+ name,
        icon:'success'
      })
          setAuthen(true)
  
          navigate('/')
          setName('')
          setPass('')
  }
  }


return(
    <>
     

















     
    <div className='Patientlogin'>
    <h1>Login page  for Patient</h1>

     
        <input 
        type='text'
        required
         placeholder='Enter your userName'
         name='userName'
           
         
         />
        <input
         type='password'
          placeholder='Enter your password' 
          name='password'
          required
          />
       
       
           
       
            
         

              <p   
              type='submit'onClick={(e)=>{ setAuthenUser(true);
             e.preventDefault() }}>
              
              <Link exact to='/'>Login</Link>
            
               </p>

               
            
           
        
    </div>
    
    </>
)

}
